import com.google.gwt.requestfactory.shared.RequestContext;
import medizin.client.managed.request.ApplicationRequestFactory;
import medizin.client.managed.request.McProxy;
import medizin.client.managed.request.McRequest;
import medizin.client.managed.ui.McDetailsView;
import medizin.client.managed.ui.McEditView;
